/*AFFICHAGE MENU*/

#menu{
  top: 0;
  left: 0;
    position: absolute;
    z-index: 10;
    width: 33vw;
    padding: 125px 50px 50px;
    height: 100vh;
    background: white;
    list-style: none;
    /* pour le mettre sur la gauche
    transform: translate(-150%,0);
    -webkit-transform: translate(-150%,0);
    */
    left: -33vw;
    transition: left .5s linear;
    cursor: pointer;

    &.is-active {
      left: 0;
    }
}
